    return app.send_static_file('index.html')

# Rota catch-all para SPA routing
@app.route('/<path:path>')
def serve_spa(path):
    # Para arquivos estáticos específicos
    if '.' in path:
        return app.send_static_file(path)
    # Para rotas do SPA, sempre retorna o index.html
    return app.send_static_file('index.html')

